import * as React from 'react';

// material-ui
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions, Grid, Stack, Avatar } from '@mui/material';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';

import bg_profile from 'assets/images/bg/bg-profile.jpeg';

// project import
import MainCard from 'components/MainCard';

import avatar1 from 'assets/images/users/avatar-jack.jpg';
// ==============================|| Edit Profile PAGE ||============================== //
const user = JSON.parse(localStorage.getItem('user'));
const EditProfile = () => {
    const [value, setValue] = React.useState(0);

    const handleChange = (event: React.SyntheticEvent, newValue: number) => {
        setValue(newValue);
    };

    return (
        <>
            <MainCard title="Edit Profile Card">
                <Card sx={{ maxWidth: '100%' }}>
                    <CardActionArea>
                        <CardMedia component="img" height="140" image={bg_profile} alt="green iguana" />
                        <CardContent>
                            <CardContent sx={{ px: 2.5, pt: 3 }}>
                                <Grid container justifyContent="space-between" alignItems="center">
                                    <Grid item>
                                        <Stack direction="row" spacing={1.25} alignItems="center">
                                            <Avatar alt="profile user" src={avatar1} sx={{ width: 200, height: 200 }} />
                                            <Stack>
                                                <Typography variant="h5">{user.firstName + ' ' + user.lastName}</Typography>
                                                <Typography variant="subtitle1" color="secondary.A200">
                                                    {user.userEmail}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary">
                                                    UI/UX Designer
                                                </Typography>
                                            </Stack>
                                        </Stack>
                                    </Grid>
                                </Grid>
                            </CardContent>

                            <Box sx={{ width: '100%', bgcolor: 'background.paper' }}>
                                <Tabs value={value} onChange={handleChange} centered>
                                    <Tab label="Item One" />
                                    <Tab label="Item Two" />
                                    <Tab label="Item Three" />
                                </Tabs>
                            </Box>
                        </CardContent>
                    </CardActionArea>
                    <CardActions>
                        <Button size="small" color="primary">
                            Save
                        </Button>
                    </CardActions>
                </Card>
            </MainCard>
        </>
    );
};

export default EditProfile;
